import "core-js/modules/es.array.push.js";
import api from '@/services/api';
export default {
  data() {
    return {
      orders: [],
      newOrder: {
        typeOfWaste: '',
        weight: ''
      },
      wasteTypes: [{
        id: 1,
        name: 'Plastic'
      }, {
        id: 2,
        name: 'Metal'
      }, {
        id: 3,
        name: 'Cardboard'
      }, {
        id: 4,
        name: 'Bronze'
      }],
      showForm: false,
      showDeleteModal: false,
      deleteOrderId: null,
      isEditing: false,
      editingOrderId: null
    };
  },
  created() {
    this.fetchOrders();
  },
  methods: {
    async fetchOrders() {
      try {
        const response = await api.getAllOrders();
        this.orders = response.data;
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    },
    toggleForm(show) {
      this.showForm = show;
      if (!show) {
        this.resetForm();
      }
    },
    async submitForm() {
      if (this.isEditing) {
        await this.updateOrder();
      } else {
        await this.addOrder();
      }
    },
    async addOrder() {
      try {
        const response = await api.addOrder(this.newOrder);
        this.orders.push(response.data);
        this.resetForm();
      } catch (error) {
        console.error('Error adding order:', error);
      }
    },
    async updateOrder() {
      try {
        await api.updateOrder(this.editingOrderId, this.newOrder); // Ensure editingOrderId is correctly used
        const index = this.orders.findIndex(order => order._id === this.editingOrderId);
        if (index !== -1) {
          this.orders.splice(index, 1, {
            ...this.editingOrderId,
            _id: this.editingOrderId
          });
        }
        this.resetForm();
      } catch (error) {
        console.error('Error updating order:', error);
      }
    },
    async deleteOrder() {
      try {
        await api.deleteOrder(this.deleteOrderId);
        this.orders = this.orders.filter(order => order._id !== this.deleteOrderId);
        this.closeDeleteModal();
      } catch (error) {
        console.error('Error deleting order:', error);
      }
    },
    confirmDeleteOrder(order) {
      this.showDeleteModal = true;
      this.deleteOrderId = order._id;
    },
    closeDeleteModal() {
      this.showDeleteModal = false;
      this.deleteOrderId = null;
    },
    editOrder(order) {
      this.newOrder = {
        ...order
      };
      this.editingOrderId = order._id; // Ensure editingOrderId is set correctly
      this.isEditing = true;
      this.showForm = true;
    },
    cancelEdit() {
      this.resetForm();
    },
    resetForm() {
      this.newOrder = {
        typeOfWaste: '',
        weight: ''
      };
      this.isEditing = false;
      this.editingOrderId = null;
      this.showForm = false;
    }
  }
};
